package com.example.greensnap.dbconexion

import android.content.Context
import android.database.sqlite.SQLiteDatabase
import android.database.sqlite.SQLiteOpenHelper
import androidx.core.content.ContextCompat.getString
import com.example.greensnap.R

class DBManager(private val context:Context, dbName:String, factory:SQLiteDatabase.CursorFactory?, dbVersion:Int): SQLiteOpenHelper(context,dbName,factory,dbVersion) {

    companion object {
        val DB_NAME = "greensnap.db3"
        val DB_VERSION = 1
    }

    constructor(context:Context):this(context, DB_NAME, null, DB_VERSION){}
    //Creacion de las tablas
    override fun onCreate(db: SQLiteDatabase) {

        db.execSQL("CREATE TABLE CUIDADOS (CODIGO_CUIDADOS INTEGER PRIMARY KEY NOT NULL, ILUMINACION TEXT, TEMPERATURA TEXT, SUSTRATO TEXT, RIEGO TEXT, HUMEDAD TEXT, ABONO TEXT, PODA TEXT, ENFERMEDADES TEXT, TRANSPLANTE TEXT)")
        db.execSQL("CREATE TABLE CATEGORIAS (ID_CATEGORIA INTEGER PRIMARY KEY AUTOINCREMENT NOT NULL, NOMBRE_CATEGORIA TEXT, DESCRIPCION TEXT, CODIGO_CUIDADOS INTEGER, FOREIGN KEY (CODIGO_CUIDADOS) REFERENCES CUIDADOS(CODIGO_CUIDADOS))")
        db.execSQL("CREATE TABLE PLANTAS(ID_PLANTA INTEGER PRIMARY KEY AUTOINCREMENT NOT NULL, NOMBRE_PLANTA TEXT, TIPO TEXT, IMAGEN TEXT, ID_CATEGORIA INTEGER, FOREIGN KEY (ID_CATEGORIA) REFERENCES CATEGORIAS(ID_CATEGORIA))")
        db.execSQL("CREATE TABLE JARDIN (ID_PLANTA INTEGER PRIMARY KEY NOT NULL, NOMBRE_PLANTA TEXT, IMAGEN TEXT, ID_CATEGORIA INTEGER)")
        db.execSQL("CREATE TABLE USUARIOS (ID_USUARIO INTEGER PRIMARY KEY AUTOINCREMENT NOT NULL, NOMBRE_USUARIO TEXT, CORREO TEXT, PASSWORD TEXT)")

        db.execSQL("INSERT INTO USUARIOS (NOMBRE_USUARIO, CORREO, PASSWORD) VALUES ('admin', 'admin@admin.com', 'admin')")

        db.execSQL("INSERT INTO CUIDADOS (CODIGO_CUIDADOS, ILUMINACION, TEMPERATURA, SUSTRATO, RIEGO, HUMEDAD, ABONO, PODA, ENFERMEDADES, TRANSPLANTE) VALUES (10, 'Lugares con luz indirecta, especialmente en las horas centrales del día. Un lugar cerca de una ventana orientada hacia el este o el oeste es ideal.', 'Entre 13-15ºC', 'Necesita un sustrato ácido y bien drenado. Puedes mezclar musgo de turba, atena y perlita para asegurar un buen drenaje y rentención de humedad', 'Mantener el suelo de la maceta húmedo pero no mojado. Regarla cuando la capa superior del sustrato esté seca al tacto con agua no alcalina (se puede filtrar o recoger agua de lluvia). Evitar el encharcamiento y el exceso de agua, ya que puede causar enfermedades y pudirición en la planta. Si hace calor se riega cada 2 días.', 'La planta necesita un ambiente con mucha humedad, para ello, puedes colocar la maceta sobre un plato lleno de agua con piedras que evitarán que la maceta toque el agua, o bien pulverizar las hojas con agua destilada', 'Aplicar un fertilizante específico para plantas acidófilas durante la temporada de crecimiento (primavera-verano) cada 2-4 semanas siguiendo las instrucciones del fabricante para evitar quemar las raices.', 'Utiliza herramientas lismpias y bien afiladas para evitar desgarros en las ramas y propagación de enfermedades. Realiza los cortes justo por encima de un brote o yema, en un ángulo de 45 grados. Retirar cualquier rama muerta, enferma o dañada, así como aquellas que estén entrelazadas en el centro de la planta permitiendo así mejora la circulación del aire y la entrada de luz. Requiere un periodo de descanso para volver a florecer, por lo que deberá colocarla en un lugar fresco (13-15ºC) durante 6-8 semanas evitando las corrientes de aire frio y los cambios bruscos de temperatura.', 'Suele ser susceptible a plags como pulgones, ácaros y cochinillas, así como enfermedades fúngicas. Inspecciona regularmente tu planta y trata cualquier infestación o enfermedad con los productos adecuados.', 'Es necesario un transplante anual en exterior. Durante el verano en sitio sombrio a principios de junio hacemos un agujero lo bastante grande para que qupa la planta con su maceta (sí maceta incluida) controlando aún mas los riegos y la humedad. A principios de Septiembre se desentierra y si vemos que las raices sobresalen por debajo, cambiamos a una maceta más grande con mucho cuidado.')")
        db.execSQL("INSERT INTO CUIDADOS (CODIGO_CUIDADOS, ILUMINACION, TEMPERATURA, SUSTRATO, RIEGO, HUMEDAD, ABONO, PODA, ENFERMEDADES, TRANSPLANTE) VALUES (20, 'Colocarla cerca de una ventana. Evitar exponerla directamente al sol ya que puede quemar sus hojas.', 'Requiere una temperatura cálida y estable (18-24ºC). Evitar corrientes de aire fío y fuentes de calor intenso como radiadores.', 'Utiliza un sustrato bien drenado y rico en  materia orgánica. Puedes mezclar tierra para macetas con turba o perlita para mejorar la textura y el drenaje.', 'No tolera el agua estancada. Permite que la capa superior del suelo se seque antes de volver a regar. Controla la humedad del sustrato y evita exceso de agua.', 'Le gusta la humedad ambiental por lo que es recomendable colocar un plato con agua cerca de la maceta o utilizar un humidificador en ambientes secos.', 'Utilizar un fertilizante líquido balanceado pra begonias un vez al mes durante la temporada de crecimiento (primavera-verano). Sigue las instrucciones del fabricante para evitar el exceso y dañar las raices.', 'Retirar la flores marchitas y hojas amarillentas para promvoer un crecimiento saludable. Hazlo con herramientas limpias y bien afiladas.', 'Es susceptible a placagas como ácaros, pulgones, y cochinillas. Inspecciona regularmente tu planta utilizando insecticidas orgánicos si es necesario y evita el exceso de humedad para prevenir enfermedades fúngicas.', 'No es necesario hasta que las raices salgan de la maceta, en ese caso cambiar a una maceta mas grande con mucho cuidado. Intenta hacerlo en primavera.')")
        db.execSQL("INSERT INTO CUIDADOS (CODIGO_CUIDADOS, ILUMINACION, TEMPERATURA, SUSTRATO, RIEGO, HUMEDAD, ABONO, PODA, ENFERMEDADES, TRANSPLANTE) VALUES (30, 'Necesita estar en un lugar en el que reciba sombra de forma parcial, prolo que una ventana es la mejor opción. Si en verano la pones en exterior tiene que ser evitando el sol de forma directa.', 'Entre los 15-22ºC', 'Utiliza un sustrato muy bine drenado y rico en nutrientes. Puedes hacerlo con una mezcla de turba con mantillo o tierra de brezo y perlita, vermiculita o grava volcánica para mejorar el drenaje.', 'Necesita una media de 2 riegos a la semana sobre tod en su época crecimiento (primavera-verano). El resto del año debes regarlo con menos frecuencia.', 'Al menos del 50%, algo que se puede conseguir pulverizando las hojas cada 2-3 días cuando el ambiente esté seco.', 'Aplícalo en primavera y verano añadiendo abono líquido al riego una vez al mes. (siguiendo las instrucciones del fabricante)', 'Esta planta no necesita poda, pero deberás retirar las hojas en mal estado o dañadas. Utiliza herramientas limpias y bien afiladas.', 'Los pulgones y cochinillas son los que más afecatan a esta planta, podrás eliminarlos con una mezcla elaborada a base de auga y jabón.', 'Deberás hacerlo en primavera y cada dos años, utilizando siempre una maceta más amplia para que esos dos años pueda crecer sin problemas.')")
        db.execSQL("INSERT INTO CUIDADOS (CODIGO_CUIDADOS, ILUMINACION, TEMPERATURA, SUSTRATO, RIEGO, HUMEDAD, ABONO, PODA, ENFERMEDADES, TRANSPLANTE) VALUES (40, 'Necesitan una habitación luminosa, sin embargo debes mantenerla alejada de las ventanas y las fuentes de luz directa. En invierno cámbiala a un lugar donde reciba más luz. Si notas que las hojas se secan o muestran manchas marrones significa que recibe demasiada luz. Evita ponerla ceca de estufas, chimeneas o radiadores.', 'Entre 16-21ºC en meses cálidos y no dejar que baje de 13ºC en invierno. Si baja de 10ºC es posible que la pierdas.', 'Es muy importante que tenga buen drenaje. Lo mjor es usar la mezcla universal para semilleros (turba, fibra de coco y humus de lombriz a partes iguales, y añadir un puñado de vermiculita y otro de perlita para favorecer el drenaje.', 'Dos o tres veces por semana en cantidades pequñas para mantener la humedad y evitar la pudrición de las raices. En meses cálidos no dejes que la capa supeficial del sustrato se seque, en invierno puedes dejar que se seque ligeramente.', 'Necesita un nivel de humedad ambiental muy alto por lo que debes pulverizar de vez en cuando las hojas o poner la maceta sobre un plato de agua y piedras para evitar el contacto con el agua (deberás cambiar el agua un par de veces por semana) o bien ponerla en el baño, ya que suele ser muy luminoso y el nivel de humedad es muy alto.', 'Puedes aportar 1 o 2cm de humus de lombriz a la base de los tallos una vez al mes en temporada de crecimiento (Abril-Septiembre)', 'No es necesario podar, sólamente quitar hojas marchitas o enfermas.', 'Si las hojas están quemadas será por exceso de luz, si están amarillas es por falta de nutrientes o falta de luz y si las hojas tienen un color mas oscuro es exceso de agua. Es propensa a contraer araña roja, se manifiesta debilitando las hojas y telarañas finas. También puede infestarse con cohinillas arenosas o insectos cerosos.', 'Cuando las raices sobresalgan de la maceta, cambiar con mucho cuidado a una maceta más grande')")
        db.execSQL("INSERT INTO CUIDADOS (CODIGO_CUIDADOS, ILUMINACION, TEMPERATURA, SUSTRATO, RIEGO, HUMEDAD, ABONO, PODA, ENFERMEDADES, TRANSPLANTE) VALUES (50, 'Requiere exposición solar completa, auque puede tolerar tambien luz indirecta. Podremos ver una orilla de color rojo en las hojas muy llamativas y también se podrán desarrollar flores cuando está con exposición solar completa. Bajo sombra no podrán verse estos dos caracteres especiales.', 'Entre los 12-26ºC. No se recomienda para zonas exteriores donde haya heladas (es capaz de aguantar heladas de hasta -2ºC sin inmutarse, pero con el sustrato seco para evitar daños en las raices).', 'Necesita un sustrato que tenga buen drenaje y que retenga el agua en cierta medida. Puedes hacer una mezcla a partes iguales de perlita, vermiculita y tepojal con otra de turba.', 'Necesita un riego moderado. Lo que nos indica cuándo hay que regala es el sustrato, deberás esperar a que esté seco para regar nuevamente.', 'Requiere poca humedad, mas bien baja.', 'Esta planta no necesita muchos nutrientes para su desarrollo, por lo que se recomienda utilizar fertilizante líquido 2-3 veces duande la temporada de crecimiento (primavera-verano-otoño).', 'Cuando la planta tenga mas de 1 año de antigüedad deberás retirar las ramas demasiado finas y largas que soportan el peso de las hojas, de esta manera crecerán nuevas mas gruesas que permitirá que la planta sea mas alta y ancha (notarás cuando debes hacerlo cuano empiecen a decaerse las ramas hacia abajo). Debes hacerlo en primavera o a principios de verano.', 'El mayor enemigo de esta planta es la humedad. Un exceso de riego , sobre todo si lo cominas con las temperaturas frías, hará que sus raíces se pudran y propiciarán la aparicón de hongos. Respecto a las plagas sus hojas son una de las comidas de las cochinillas. Aunque una invasión de estos insectos no suele poner en peligro la vida de la planta, es recomendable eliminarlas cuanto antes aplicando un fitosanitario adecuado. (También es suceptible de ser atacada por pulgones.', 'El mejor momento es durante la primavera o el verano. Riega ligeramente el sustrato para falcilitar la extracción. Una vez fuera examina las raíces y elimna cualquier raíz muerta o podrida. Coloca una capa delgada de sustrato nuevo en el fondo del recipiente y coloca el árbol de jade en el centro y rellena de sustrato hasta cubrir todas las raices. Presiona ligeramente el sustrato para asentarlo. Evita regar de inmediato, debes esperar al menos una semana para permitir que las raíces se adapten a su nuevo entorno. Cuando pase la semana regar moderadamente evitando el encharcamiento.')")
        db.execSQL("INSERT INTO CUIDADOS (CODIGO_CUIDADOS, ILUMINACION, TEMPERATURA, SUSTRATO, RIEGO, HUMEDAD, ABONO, PODA, ENFERMEDADES, TRANSPLANTE) VALUES (60, 'Necesita mucha luz solar, por lo que una ventana orientada al este o al oeste es lo ideal. En caso que no puedas puedes utilizar una lámpara para plantas. Si la pones en exterior lo mejor es que tenga sombra parcial, le puede dar el sol directo pero sólo cuando se haya acostumbrado al clima de la zona y a tus cuidados.', 'Entre 20-35ºC. En invierno debe mantenerse por encima de los 0ºC. Evitaremos las corrientes de aire.', 'Esta planta necesita un sustrato ácido y que retenga humedad. Esto lo conseguimos con una mezcla de fibra de coco, perlita y musgo sphagnum.', 'Riégala siempre con agua destilada o de lluvia. Mantén siempre la tierra húmeda. Se debe regar mediante riego invertido, es decir dejando un dedo de agua en el plato de la maceta lo que mantendrá el sustrato húmedo por capilaridad. El método idóneo es dejar es dedo de agua en todo momento hasta que se consuma completamente, dejar reposar dos días y repetir el proceso.', 'Necesita una humedad alta, preferiblemente sin pulverización. Puedes utilizar un plato con agua cerca de la maceta o colocar una pequeña cúpula de cristal o plástico que la hermetice.', 'No debemos fertilizarla ya que los abonos tradicionales quemarían sus raices. Lo que suplanta su abono, y que actua como tal, es la captura de los insectos que atrapa. No hace falta que capturemos nosotros a sus presas ya que la planta a desarrollado un método natural para atraerlas por sí sólas. Si aún no resistimos la tentación deberemos darle insectos vivos ya que su movimiento al ser atrapados es los que provoca la liberación de los jugos gástricos de la planta. También tenemos que tener cuidado al accionar sus trampas frecuentemente por error o entretenimiento, ya que el cierre  de las trampas acarrea mucho estrés a la planta y podemos llegar a matarla.', 'Deberemos hacerlo en primavera ya que está comenzando su ciclo de crecimiento y le permite recuperarse rápidamente y concentrar su energía en el nuevo crecimiento. Examinamos la planta para identificar las trampas ennegrecidas, marrones o que muestren signos de daño, eliminamos el material con herramientas bien desinfectadas y afiladas, y nos desacemos del material retirado lejos de la planta para evitar cualquier posible enfermedad o atracción de plagas en descomposición.', 'Esta planta se ve afectada sobre todo por podredumbre de las hojas (es una enfermedad fúngica que provoca el marchitamiento y la muerte de sus hojas), mancha marron (es una enfermedad fúngica que causa manchas marrones en sus hojas, provocando la muetr de las hojas e incluso es capaz de inhibir la capacidad de la planta para atrapar insectos), marchitamiento de las hojas (es una enfermedad provocada por exceso de agua o plagas, se caracteriza por el marchitamiento progresivo de la planta)', 'El momento adecuado es en primavera o principios de verano. Lo ideal es trasplantarla cada 2-3 años, o cuando la planta supere el tamaño de su contenedor actual.')")
        db.execSQL("INSERT INTO CUIDADOS (CODIGO_CUIDADOS, ILUMINACION, TEMPERATURA, SUSTRATO, RIEGO, HUMEDAD, ABONO, PODA, ENFERMEDADES, TRANSPLANTE) VALUES (70, 'Necesita una exposición solar completa. Puede tenerse también en semisombra si recibe alguan horas de sol completo durante el día, o en interiores si se coloca cerca de una ventana.', 'Entre los 18-26ºC. Puede soportar hasta 0ºC cuando el sustrato esté seco entre 1-2 días, aunque es mejor evitarlo.', 'Debe ser drenante ya que de la retención de agua ya se encargan las hojas. Puedemos preparar una mezcla de sustrato pra suculentas o cactáceas con partes iguales de humus de lombriz, turba, y un material poroso que puede ser vemiculita, tepojal o grava volcánica.', 'Ya que almacena agua en el tejido de sus hojas, es de bajo riego, por lo que debe regarse cuando el primer centímetro del sustrato se seque. En invierno es mejor hacerlo cada dos semanas para evitar que las hojas se arruguen. No debe regarse por encima de la roseta porque esto causará pudriciones. El método de riego es mediante inmersión, en donde se sumerge la maceta en un recipiente con agua para que abosrba lo que necesite ya que regar sobre el sustrato suele ser más dificil por el crecimiento ancho propio de esta especie.', 'Evitar un ambiente con humedad, ya que de este modo la planta es más susceptible a contraer enfermedades fúngicas.', 'En primavera y en verano puede aplicarse un fertilizante bajo en nitrógeno, aunque no es indispensable.', 'La poda no es estrictamente necesaria, pero si notas hojas muertas o dañadas, puedes retirarlas para mantener la planta saludable. Siempre en su época de crecimiento (primavera-verano)', 'Las plagas más comunes que pueden afectar a esta planta son los pulgonse y las cochinillas que puedes eliminar aplicando insecitida natural o jabón insecticida para los pulgones y utilizar un hisopo de algodón empapado en alcohol isopropílico para las cochinillas. Las enfermedades que más afectan a esta planta es la pudrición de raices por exceso de riego y el moho gris por exceso de humedad (para tratar el moho gris puedes utilizar fungicidas especiífocos para plantas suculentas). ', 'Debes hacerlo en primavera o verano siempre y cuando la planta haya superado el tamaño de su maceta actual o se vean signos de deterioro en la calidad del sustrato (que el sustrato se encuentre compacto y no facilite el drenaje).')")
        db.execSQL("INSERT INTO CUIDADOS (CODIGO_CUIDADOS, ILUMINACION, TEMPERATURA, SUSTRATO, RIEGO, HUMEDAD, ABONO, PODA, ENFERMEDADES, TRANSPLANTE) VALUES (80, 'Necesita luz solar directa y florecerá con abundancia siempre que reciba tres o cuatro horas de luz solar durante el día. En exterior, procuraremos colocarla a pleno sol, en interior, durante el invierno, ubicaremos la planta en un lugar orientado al sur o al oeste.', 'Entre 18-23ºC. Puede tolerar temperaturas nocturnas de hasta 10ºC.', 'Si la plantas al aire libre hazlo en un suelo bien drenado. Si la plantas en interior, necesita una mezcla de sustrato para cactáceas, con buen drenaje (turba con mantillo o tierra de brezo con perlita, vermiculita o grava volcánica). No debe plantarse en un recipiente mayor que cepellón y el exceso de tierra retendrá agua y puede provocar que las raíces se pudran.', 'No requiere mucha agua. En exterior le bastará con agua de lluvia, en maceta rigaremos cuando el sustrato se seque por completo. Hazlo abundantemente y deja que drene todo el auga sobrante evitando el encharcamiento ni el sustrato húmedo por períodos prolongados. Durante el invierno no necesita apenas riego.', 'Generalmente por debajo del 50%,por lo que necesita un nivel de humedad mas bien bajo. Utilizaremos un pulverizador cada 3-4 dias.', 'Utilizaremos un fertilizante líquido equilibrado para plantas de interior desde primavera hasta otoño, abonandola con el agua un par de veces al mes.', 'No necesita poda, pero puedes retirar las hojas y ramas marchitas en otoño, para que en la próxima temporada estimule su crecimiento', 'No es propensa a plagas y enfermedades, pero aún así puede sufrir los problemas más comunes de plantas de interior como los pulgones o cochinillas (se pueden eliminar con remedios caseros). Tambíen pueden aparecer enfermedades fúngicas como la brotitis que produce manchas en las hojas o la pudrición de la raíz (deja que la tierra se seque, antes de volver a regar, es lo mejor para prevenir este problema).', 'Cuando la planta sea demasiado grande la cambiaremos a otra maceta con mucho cuidado o al jardín como prefieras, en cualquier caso necesitas un sustrato con muy buen drenaje.')")
        db.execSQL("INSERT INTO CUIDADOS (CODIGO_CUIDADOS, ILUMINACION, TEMPERATURA, SUSTRATO, RIEGO, HUMEDAD, ABONO, PODA, ENFERMEDADES, TRANSPLANTE) VALUES (90, 'Requiere luz indirecta y abundante. Colócala cerca de una ventana con cortinas ligeras para filtrar los rayos del sol.', 'Entre 18-24ºC. Evitaremos exponerla a corrientes de aire frío o calor extremo.', 'Utilizaremos una mezcla ligera bien drenada compuesta por turba, perlita y fibra de coco. Esto asegurará un buen drenaje y evitará el encharcamiento de las raíces.', 'Necesita un riego constante pero moderado. Mantendremos el sustrato simpre ligeramente húmedo, evitando encharcarlo. Es importanet regar directamente sobre las hojas y no en el centro de la roseta, para evitar el encharcamiento de las raíces.', 'Necesita un ambiente húmedo. Podemos aumentar la humedad colocando la maceta sobre un plato con agua y piedras o utilizando un humificador.', 'Durante la época de crecimiento (primavera-verano), podemos fertilizarla 1 vez al mes utilizando fertilizante líquido equilibrado siguiendo las instrucciones del fabricante.', 'Puede podarse en cualquier época del año, sin embargo, es importante asegurarse de que la planta no está demasiado estresada por factores ambientales o enfermedades antes de podarla.', 'Las enfermedades y plagas que más afectan a esta especie son manchas foliares (es una enfermedad fúngica que provoca manchas o decoloraciones en las hojas), podredumbre de raíz (raices débiles o dañadas, provoca el marchitamiento de las hojas, o la planta se vuelve marón y se ablanda la base), cochinillas(insectos pequeños que aparecen como manchas blancas o gries en las hojas) y ácaros (pequeños y difíciles de ver a simple vista, sin embargo las hojas se decoloran o tienen un aspecto seco).', 'Debemos hacerlo entre mediados y finales de primavera. Esto permite a la planta echar raíces antes del verano y le proporciona la oportunidad de crecer y fortalecerse para soportarlo.')")
        db.execSQL("INSERT INTO CUIDADOS (CODIGO_CUIDADOS, ILUMINACION, TEMPERATURA, SUSTRATO, RIEGO, HUMEDAD, ABONO, PODA, ENFERMEDADES, TRANSPLANTE) VALUES (100, 'Necesita una buena cantidad de luz solar directa para florecer adecuadamente. Coloca tu planta en un lugar donde reciba al menos 6 horas de luz solar al día.', 'Entre 15-35ºC. Puede soportar una temp. mínima de -10ºC en invierno y una máxima de 45ºC en verano.', 'Necesita un sule levemente alcalino pero que no tenga nada de arcilla con un buen drenaje.', 'Asegúrate de regar la planta cuando la capa superficial del suelo esté seca, evitando echarcamientos que puedan dañar sus raíces.', 'Es recomendable mantener un nivel moderado, evitando la humedad excesiva o el ambiente seco. Podemos utilizar un pulverizador para ello.', 'Aplica un abono para plantas de flor o un compost en primavera para estimular la floración, especialmente en el caso de los claveles de maceta.', 'La poda regular es importante para mantener el clavel en buena forma. Retiraremos las flores marchitas y las ramas muertas o debilitadas para estimular un crecimiento saludable.', 'Las plagas y enfermedades que afectan a esta planta son las babosas (se alimentan de todas sus partes), los trips (se alimentan del envés de las hojas, son como tijeras muy pequeñas de color negro), los pulgones (son de color rojo, verde o marron y se alimentan de las células de las hojas y de  los capullos florales), el tortrix europeo (es un lepidóptero cuys larvas se alimentan de las flores), los minadores (son dípteros cuyas larvas excavan galerías en las hojas), la roya (es una enfermedad fúngica que produce manchas de color anaranjado en las hojas y tallos), el fusarium (es una enfermedad fúngica que pudre las raíces y va debilitando el tallo) y el virus del jaspeado del clavel (es un virus que produce manchas pequñas en líneas o anillos de color pardo o púrpura)', 'Se hacen de marzo-mayo, mayo-junio y en otoño, aunque se puede hacer en otras épocas del año simpre y cuando se controle la temperatura, la humedad y la luminosidad.')")
        db.execSQL("INSERT INTO CUIDADOS (CODIGO_CUIDADOS, ILUMINACION, TEMPERATURA, SUSTRATO, RIEGO, HUMEDAD, ABONO, PODA, ENFERMEDADES, TRANSPLANTE) VALUES (110, 'Estas preciosas plantas tienen que estar ubicadas a pleno sol, de lo contrario pueden no florecer, si consiguen hacerlo, las flores no se abrirán adecuadamente.', 'Temperatura agradable unos 20ºC. Puede llegar a tolerar los -5ºC en invierno con el sustrato completamente seco.', 'Es importante que la tierra en la que esté plantada sea capaz de absorber el agua. Puedes usar el universal mezclado con perlita. Si tiene problemas de drenaje deberemos tomar medidas, instalando sistemas de drenaje: hacer un agujero de plantación de 50x50cm (recomiendo 1x1m si tu jardín se suele inundar cuando llueve mucho o de forma torrencial), cubrir sus lados (excepto la base) con malla de sombreo y echa una capa gruesa, 20cm como mínimo, de arlita o greda volcánica. Después se termina de rellenar con sustrato universal.', 'Se tiene que regar cada 2-3 días en verano, y 1-2 veces por semana el resto del año.', 'Evitar el exceso y la falta de humedad, debe estar nivelado.', 'Deberemos hacerlo durante la temporada de crecimiento (primavera-otoño). Se puede utilizar cualquier abono o fertilizante universal para jardín.', 'No es necesario podarla, pero sí retirar hojas dañadas, enfermas o marchitas para favorecer un crecimiento sano.', 'Puede sufrir el ataque del pulgón. Si esto sucede, se puede esperar a que aprite el sol y el propio calor se encargue de acabar con la plaga o bien solucinar el problema con tratamientos ecológicos como el jabón potásico o el aceite de Neem. Totalmente recomendado solucionar el problema nosotros mismos en cuando se ven indicios.', 'Es importante que dé el sol de manera directa durante todo el día, por lo que no se debe poner cerca de plantas u otros elementos que le den sombra. Lo mejor es hacerlo en primavera.')")
        db.execSQL("INSERT INTO CUIDADOS (CODIGO_CUIDADOS, ILUMINACION, TEMPERATURA, SUSTRATO, RIEGO, HUMEDAD, ABONO, PODA, ENFERMEDADES, TRANSPLANTE) VALUES (120, 'Es importante colocarlas en zonas muy luminosas, donde reciba una buena cantidad de luz solar. Si el clima es muy cálido o el sol muy intenso, es recomendable ubiccarlas en simsombra.', 'Aguanta un rango muy amplio de temperaturas, desde las más cálidas a las heladas no demasiado intensas.', 'Precisan de una tierra bastante rica en nutrientes. Puedes usar turba junto con vemiculita y perlita para tener un buen drenaje.', 'En época de floración será necesaio un riego cada 2-3 días según las características concretas del clima y el suelo. El resto del año deberemos mantener la tierra siempre relativamente húmeda, pero nunca encharcada. Un riego excesivo o  una tierra con un drenaje insuficiente podrían provocar exceso de humedad en sus raíces, y que la planta enferme o se debilite.', 'Necesita un ambiente con humedad equilibrada. Pudemos conseguirlo poneindo la maceta encima de un plato con agua y piedras o con un pulverizador.', 'Se recomienda el humus de lombriz pos su composición que es muy beneficioso tanto para la planta como para el suelo.', 'Tiende a perder gran parte de vitalidad cuando llegan los meses de calor tras su florecimiento, siendo habitual que la planta muera si no se hace nada. Para evitar esto, y conseguir que nuestra flor viva como una planta perenne en lugar de como una bianual, será necesario relaiza una poda agresiva en cuanto las temperaturas suba, dejando sólo el tallo.', 'Una de las principales amenazas para esta planta es el ataque de los pulgones y los hongos como el oídio que es con el que hay que tener más cuidado.', 'El periodo perfecto es durante otoño-primavera permitiendo a la planta establecer raíces fuertes antes del calor del verano.')")

        db.execSQL("INSERT INTO CATEGORIAS (NOMBRE_CATEGORIA, DESCRIPCION, CODIGO_CUIDADOS) VALUES ('Azaleas', 'Planta originaria china de la familia de los rododendros, requier riegos cuidadosos y no soporta el agua con cal. Quizá por eso sobrevive tan poco y es uno de los mayores retos de los amantes de las plantas. Es una planta de crecimiento lento, suele florecer de abril a mayo, le gustan las temperaturas frescas y si hace calor necesita riego cada dos dias. Además es necesario unb transplante anual exterio.', 10)")
        db.execSQL("INSERT INTO CATEGORIAS (NOMBRE_CATEGORIA, DESCRIPCION, CODIGO_CUIDADOS) VALUES ('Begonias', 'Las begonias son plantas populares por su belleza y diversidad de colores y formas. Sus cuidados son semisombra, sustrato drenante y riego moderado. El cultivo de las begonias no entraña grandes dificultades. Existe más de 2.000 especies y bajo ellas, numerosas variedades. Florecen a principios de verano y acaban su ciclo con las primeras heladas de otoño', 20)")
        db.execSQL("INSERT INTO CATEGORIAS (NOMBRE_CATEGORIA, DESCRIPCION, CODIGO_CUIDADOS) VALUES ('Cactus de Navidad', 'Pertenece a la familia de las cactáceas, es una planta originaria de América Central y del Sur. Aunque tiene un aspecto distinto a la mayoria de cactus, lo que más le diferencia son sus flores, que tienen forma de plumas, de ahí su nombre común de plumas de Santa Teresa, además florecen antes que la mayoría de especies. Las flores pueden ser rosas, rojas, blancas, naranjas o amarillas y miden entre 3 y 5 cm de diámetro. Es uno de los catus más duraderos, ya que en maceta tiene una vida de entre 3 y 10 años. Su cultivo es sencillo, aunque tiene en contra que algunos de sus segmentos se pueden estropear sin razón aparante, por lo que hay que prestar atención a sus cuidados y eliminar las partes que se ven mal.', 30)")
        db.execSQL("INSERT INTO CATEGORIAS (NOMBRE_CATEGORIA, DESCRIPCION, CODIGO_CUIDADOS) VALUES ('Calatheas', 'Es una planta tropical de la famila de las marantas, según la especie la planta adopta tamaños , coleres y formas direfentes en sus hojas. Las hojas nacen enrolladas y después se van abriendo, los tallos crecen en grupos, son perennes y sus hojas tienen una habilidad llamada nioctinasia, en donde las hojas se abren en el día para buscar el sol y se cireran en la noche para reducir la pérdida de agua.', 40)")
        db.execSQL("INSERT INTO CATEGORIAS (NOMBRE_CATEGORIA, DESCRIPCION, CODIGO_CUIDADOS) VALUES ('Crassula', 'Pertenece a la familia de las plantas de hojas gruesas (Crassulaceae). También se ubica la subfamilia de Crassuloideae. Este árbol crece hasta una altura máxima de aproximadamente 3 metros. Es de tallo grueso y recto, y tiene muchas ramas. En su base, a menudo está fuertemente ramificada. Es una planta suculenta muy conocida en todas partes y es muy fácil de conseguir, crece con facilidad y es bastante fácil de cuidar.', 50)")
        db.execSQL("INSERT INTO CATEGORIAS (NOMBRE_CATEGORIA, DESCRIPCION, CODIGO_CUIDADOS) VALUES ('Dionaea Muscipula', 'Llamada Diana atrapamoscas en algunos paises como Sudamérica y mas comúnmente conocida como Venus atrapamoscas, es uno de los tipos de plantas carnívoras más emblemáticos. Se trata de la única especie que conforma el género Dionaea, que tiene su origen en Carolina del Norte. Sus hojas crecen desde un rizoma enterrado, forman una roseta con una longitus que se encuentra comprendida entre los 3cm y los 8cm, que crecen de forma horizontal. En la época de floración en primavera, da lugar a un tallo floral que crece desde el mismo centro de la planta, quw es de hata 20cm de altura y con una llamativa flor blanca de pequeño tamaño en el extremo.', 60)")
        db.execSQL("INSERT INTO CATEGORIAS (NOMBRE_CATEGORIA, DESCRIPCION, CODIGO_CUIDADOS) VALUES ('Echeveria', 'También llamada rosa de alabastro, en en realidad todo un género de plantas crasas que forma parade de la familia Crassulaceae. Es originaria de México y Brasil, con cerca de 400 especies, muy resistente a las condiciones de sequía y las variaciones térmicas, sus tallos alcanzan alturas de hata 20cm y produce flores rosadas o amarillentas desde invierno a primavera.', 70)")
        db.execSQL("INSERT INTO CATEGORIAS (NOMBRE_CATEGORIA, DESCRIPCION, CODIGO_CUIDADOS) VALUES ('Euphorbia', 'Recibe su nombre común de planta corana de Cristo o planta corona de espinas debido a que existe la leyenda de que esta fue la planta que se utilizó pra la confecicón de su corana de espinas. Es originaria de Madagascar, un arbusto que llega a alcanzar, e incluso superar, alturas de 1 metro y destaca por las brácteas de color rojo o rosado de sus flores que son muy llamativas. Es una planta suculenta muy resistente, que puede llegar a florecer durante todo el año en el clima y condiciones adecuadas.', 80)")
        db.execSQL("INSERT INTO CATEGORIAS (NOMBRE_CATEGORIA, DESCRIPCION, CODIGO_CUIDADOS) VALUES ('Guzmania', 'Es una de las plantas de interior más bonitas y particulares que podemos encontrar y que resulta muy popular, además de por su belleza, por sus fáciles cuidados. Sus hojas acintadas como su inflorecencia son tremendamente decorativas.', 90)")
        db.execSQL("INSERT INTO CATEGORIAS (NOMBRE_CATEGORIA, DESCRIPCION, CODIGO_CUIDADOS) VALUES ('Claveles', 'Son originarios de Eurasia, y she han convertido en símbolo de muchos países mediterráneos por la belleza que tienen. Son hierbas perennes que florecen durante primavera y verano, entre las que se puede encontrar una gran variedad en colores y formas.', 100)")
        db.execSQL("INSERT INTO CATEGORIAS (NOMBRE_CATEGORIA, DESCRIPCION, CODIGO_CUIDADOS) VALUES ('Margaritas', 'Son una de las plantas más populares del mundo y sus flores tan características. Son tremendamente prolíficas, ye sque existen más de 140 especies distintas.', 110)")
        db.execSQL("INSERT INTO CATEGORIAS (NOMBRE_CATEGORIA, DESCRIPCION, CODIGO_CUIDADOS) VALUES ('Violas', 'También llamadas plantas del pensamiento, son originaria de Europa del norte y acutual cuenta con una enorme cantidad de vairedades, con tipos de colores y tamaños de flor. Necesitan una zona muy luminosa y mantener la tierra húmeda sin encharcar (en floración se riegan cada 2 o 3 dias). Es comestible, florece en otoño y en climás cálidos su floración puede extenderse hasta primavera.', 120)")

